        return new Source("{% extends 'base_home.html.twig' %}

{% block body %}
    <h1>Liste des articles</h1>

    <a class=\"btn btn-primary\" href=\"{{ path('article_new') }}\">
        Ajouter un article
    </a>
    <table class=\"table mt-3\">
        <thead>
        <tr>
            <th>#</th>
            <th>Catégorie</th>
            <th>Titre</th>
            <th>Date</th>
            <th></th>
        </tr>
        </thead>
        <tbody>
        {% for article in articles %}
            <tr>
                <td>{{ article.id }}</td>
                <td>{{ article.category }}</td>
                <td>{{ article.title }}</td>
                <td>{{ article.createdAt ? article.createdAt|date('Y-m-d H:i') : '' }}</td>
                <td>
                    <a class=\"btn btn-light\" href=\"{{ path('article_show', {'id': article.id}) }}\">
                        Voir
                    </a>
                    <a class=\"btn btn-primary\" href=\"{{ path('article_edit', {'id': article.id}) }}\">
                        Modifier
                    </a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">Pas d'article</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

{% endblock %}
", "article/index.html.twig", "C:\\Users\\33612\\Documents\\cours\\b2\\php-syphony\\codeblog_20-21\\templates\\article\\index.html.twig");
